        $( '#' + this.container + ' > iframe' ).css( "visibility", "visible" );

        $( '#' + name ).css( "visibility", "visible" );

        this.setActiveTab( name + OpenItems.openItemMarker );

        var item = OpenItems.getOpenItemByName( name );

