                var GROUP_REGEXP = new RegExp("\\"+$locale.NUMBER_FORMATS.GROUP_SEP,"g");
                var x = text.replace(GROUP_REGEXP, '');
                x = x.replace($locale.NUMBER_FORMATS.DECIMAL_SEP, '.');
